commits.content:
"use client";
import React, { useState } from 'react';

const testimonials = [
  {
    name: "Vishnu K N",
    role: "APS UPS Vendor",
    company: "",
    review: "I came to know about Mahavir system when I searched for vendors of APS UPS. This is the vendor provided the UPS low price in less lead time, compared with others. I have ordered UPS for my WFH requirement. The delivery happened within a week. The ordering, status update and delivery are done very professionally.",
    rating: 5
  },
  {
    name: "Ambadas Bhore",
    role: "UPS Buyer",
    company: "",
    review: "I was looking for a UPS for WiFi and modem router. I searched Amazon and Flipkart, but they could not provide me suitable options. MSP Webstore delivered me UPS in a day, Thanks!",
    rating: 5
  },
  {
    name: "Rajesh Kumar",
    role: "IT Manager",
    company: "DataTech Solutions",
    review: "UpFlair Technology provided us with reliable power conditioning solutions that have significantly improved our data center's uptime. Their technical expertise and prompt service support are exceptional.",
    rating: 5
  },
  {
    name: "Priya Sharma",
    role: "Operations Head",
    company: "Manufacturing Corp",
    review: "The customized power supply solutions from UpFlair have helped us reduce operational costs by 30%. Their team understood our requirements perfectly and delivered beyond expectations.",
    rating: 5
  },
  {
    name: "Arun Patel",
    role: "Facility Manager",
    company: "Green Energy Ltd",
    review: "Outstanding service quality! UpFlair's preventive maintenance program has kept our power systems running smoothly for over 3 years. Highly recommended for industrial applications.",
    rating: 5
  },
  {
    name: "Sneha Gupta",
    role: "Project Manager",
    company: "Tech Innovations",
    review: "From consultation to installation, UpFlair Technology demonstrated professionalism at every step. Their eco-friendly power solutions align perfectly with our sustainability goals.",
    rating: 5
  }
];

const Testimonials = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex === testimonials.length - 2 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex === 0 ? testimonials.length - 2 : prevIndex - 1
    );
  };

  const goToSlide = (index) => {
    setCurrentIndex(index);
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, index) => (
      <svg
        key={index}
        className={`h-5 w-5 ${index < rating ? 'text-yellow-400' : 'text-gray-300'}`}
        fill="currentColor"
        viewBox="0 0 20 20"
      >
        <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
      </svg>
    ));
  };

  return (
    <section className="font-sans py-20 bg-gray-50 relative overflow-hidden">
      {/* Subtle background pattern */}
      <div 
        className="absolute inset-0 bg-repeat bg-center opacity-5"
        style={{backgroundImage: "url('data:image/svg+xml,%3Csvg width=\"40\" height=\"40\" viewBox=\"0 0 40 40\" xmlns=\"http://www.w3.org/2000/svg\"%3E%3Cg fill=\"%239C92AC\" fill-opacity=\"0.4\" fill-rule=\"evenodd\"%3E%3Cpath d=\"M0 40L40 0H20L0 20M40 40V20L20 40\"/%3E%3C/g%3E%3C/svg%3E')"}}
      ></div>

      <div className="container mx-auto px-4 relative z-10">
        {/* Section Header */}
        <div className="text-center mb-16">
          <div className="mb-4">
            <span className="text-orange-500 text-sm font-semibold tracking-wider uppercase">TESTIMONIALS</span>
            <div className="h-1 w-16 bg-orange-500 rounded mx-auto mt-2"></div>
          </div>
          <h2 className="text-4xl lg:text-5xl font-bold text-gray-800 tracking-tight">
            Client's Reviews
          </h2>
        </div>

        {/* Testimonials Slider */}
        <div className="relative max-w-7xl mx-auto">
          <div className="overflow-hidden">
            <div 
              className="flex transition-transform duration-500 ease-in-out"
              style={{ transform: `translateX(-${currentIndex * 50}%)` }}
            >
              {testimonials.map((testimonial, index) => (
                <div key={index} className="w-1/2 flex-shrink-0 px-4">
                  <div className="bg-white rounded-2xl shadow-lg p-8 h-full transform hover:scale-105 transition-all duration-300 hover:shadow-xl">
                    {/* Quote Icon */}
                    <div className="mb-6">
                      <svg 
                        className="h-12 w-12 text-gray-200" 
                        fill="currentColor" 
                        viewBox="0 0 24 24"
                      >
                        <path d="M14.017 21v-7.391c0-5.704 3.731-9.57 8.983-10.609l.995 2.151c-2.432.917-3.995 3.638-3.995 5.849h4v10h-9.983zm-14.017 0v-7.391c0-5.704 3.748-9.57 9-10.609l.996 2.151c-2.433.917-3.996 3.638-3.996 5.849h4v10h-10z"/>
                      </svg>
                    </div>

                    {/* Review Text */}
                    <p className="text-gray-600 text-lg leading-relaxed mb-6 italic">
                      "{testimonial.review}"
                    </p>

                    {/* Rating Stars */}
                    <div className="flex justify-center mb-6">
                      {renderStars(testimonial.rating)}
                    </div>

                    {/* Client Info */}
                    <div className="text-center border-t pt-6">
                      <h3 className="text-xl font-bold text-gray-800 mb-1">
                        {testimonial.name}
                      </h3>
                      <p className="text-orange-500 font-medium mb-1">
                        {testimonial.role}
                      </p>
                      {testimonial.company && (
                        <p className="text-gray-500 text-sm">
                          {testimonial.company}
                        </p>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Navigation Arrows */}
          <button
            onClick={prevSlide}
            className="absolute left-0 top-1/2 transform -translate-y-1/2 -translate-x-4 bg-white rounded-full p-3 shadow-lg hover:shadow-xl transition-all duration-300 hover:bg-orange-50"
            aria-label="Previous testimonials"
          >
            <svg className="h-6 w-6 text-gray-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </button>

          <button
            onClick={nextSlide}
            className="absolute right-0 top-1/2 transform -translate-y-1/2 translate-x-4 bg-white rounded-full p-3 shadow-lg hover:shadow-xl transition-all duration-300 hover:bg-orange-50"
            aria-label="Next testimonials"
          >
            <svg className="h-6 w-6 text-gray-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>
        </div>

        {/* Pagination Dots */}
        <div className="flex justify-center mt-8 space-x-3">
          {Array.from({ length: testimonials.length - 1 }, (_, index) => (
            <button
              key={index}
              onClick={() => goToSlide(index)}
              className={`h-3 w-3 rounded-full transition-all duration-300 ${
                currentIndex === index 
                  ? 'bg-orange-500 w-8' 
                  : 'bg-gray-300 hover:bg-gray-400'
              }`}
              aria-label={`Go to testimonial ${index + 1}`}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
